var stageCoords={x:Stage.width/2,y:Stage.height/2, rand:280, rand2:350}
trace(stageCoords.x);



var bullet_speed = 12;
var star_speed = 12;
var bonus_speed = 0.5;
var asteroid_speed = 0.8;

var asteroid_counter = 0;

var star_counter = 0;
var bullet_counter = 0;
var enemies_counter = 0;// 
var score = 0;

var bonus_counter = 0;

var BG_DEPTH =-2;
var STAR_DEPTH = 1;
var SHIP_DEPTH = 5;// 
var BULLET_DEPTH = 4;
var ENEMY_DEPTH = 3;
var DISPLAY_DEPTH = 6; //     6 -   
var BONUS_DEPTH = 2;
var ASTEROID_DEPTH = 0;

var bgField=_root.createEmptyMovieClip("bgField",BG_DEPTH);

var starField=_root.createEmptyMovieClip("starField",STAR_DEPTH);

var shipField=_root.createEmptyMovieClip("shipField",SHIP_DEPTH);

var bulletField =_root.createEmptyMovieClip("bulletField",BULLET_DEPTH);
var bullets =[];

var enemyField =_root.createEmptyMovieClip("enemyField",ENEMY_DEPTH);
var ENEMIES_DEPTH = 0;//        

var enemies = [];

var bonusField =_root.createEmptyMovieClip("bonusField",BONUS_DEPTH); 
