var myFormat = new TextFormat();
myFormat.size = 16;
myFormat.color = 0xFFFFFF;


var displayField = _root.createEmptyMovieClip("displayField", DISPLAY_DEPTH);
var hpField = displayField.createEmptyMovieClip("hpField", 1);
