starField.onEnterFrame=function(){
	star_counter++;
	star=starField.attachMovie("star","star"+star_counter,star_counter);
	star._x = stageCoords.x+int((Math.random()*stageCoords.rand2)+(Math.random()*-stageCoords.rand2));
	star._y = stageCoords.y+int((Math.random()*-stageCoords.rand)+(Math.random()*stageCoords.rand));
	star._xscale = star._yscale = 10;
	star.xx = star._x;
	star.yy = star._y;

	if(star.yy <= stageCoords.y ){
		star.yMov = "u";
		}else{
			star.yMov = "d";
	}
	if(star.xx <= stageCoords.x ){
		star.xMov = "l";
		}else{
			star.xMov = "r";
	}
	star.onEnterFrame = starStuff;
};

function starStuff(){
	this._xscale=this._yscale+=star_speed;

	switch(this.xMov){
		case "r":
		this._x+= star_speed;
		break;
		case "l":
		this._x -= star_speed;
		break;
	}
	switch(this.yMov){
		case "u":
		this._y -= star_speed;
		break;
		case "d":
		this._y += star_speed;
		break;
	}
}
function createBG(sc:Number,xx:Number,yy:Number){
	sBg2.start();
	sBg2.setVolume(15);
	bg= bgField.attachMovie("bg4","bg4",0);
	bg._x = xx;
	bg._y = yy;
	bg._xscale = bg._yscale = sc;
	bg.onEnterFrame = function(){
		this._rotation += 0.1;
		this._xscale=this._yscale += 0.01;

	}
}

function createShip(xx:Number,yy:Number){
	ship = shipField.attachMovie("spaceShip","spaceShip",0);
	ship._x = xx;
	ship._y = yy;
	ship.dead = false;
	ship.able = true;
	ship.recovery = 0;
	ship.recoveryTime = 15;
	ship.hp = 100;
	ship.xVel = 0;
	ship.yVel = 0;
	ship.fr = 0.87;
	ship.maxVel = 7;
	ship.power = 3;
	ship.attackStr = 5;
	ship.radiusW = ship._width/2;
	ship.radiusH = ship._height/2;
	ship.canShoot = 0;
	ship.coolDown = 12;
	ship.onEnterFrame = controlShip;
}
function controlShip(){
	if(!this.dead){
		this.canShoot++;//  
		if(!this.able){
			this.recovery++;
			if(this.recovery>=this.recoveryTime){
				this.recovery = 0;
				this.able = true;

			}
		}
		if(Key.isDown(Key.UP)){
			if(Math.abs(this.yVel)<=this.maxVel){
				this.yVel -= this.power;
			}
		}
		if(Key.isDown(Key.DOWN)){
			if(Math.abs(this.yVel)<=this.maxVel){
				this.yVel += this.power;
			}
		}
		if(Key.isDown(Key.LEFT)){
			if(Math.abs(this.xVel)<=this.maxVel){
				this.xVel -= this.power;
			}
		}
		if(Key.isDown(Key.RIGHT)){
			if(Math.abs(this.xVel)<=this.maxVel){
				this.xVel += this.power;
			}
		}
		if(Math.abs(this.yVel)>=2 || Math.abs(this.xVel)>=1){
			this.gotoAndStop(2);
		}else{
			this.gotoAndStop(1);
		}
		if(Key.isDown(Key.SPACE) && this.canShoot>=this.coolDown){
		   this.canShoot = 0;
		   shoot(this._x-5,this._y);
			sLaser1.start();
			sLaser1.volume(60);
		}
		if(this._y<=this.radiusH){
			this._y = this.radiusH;
		}
		if(this._y>=Stage.height - this.radiusH){
			this._y = Stage.height - this.radiusH;
		}
		if(this._x<=this.radiusW){
			this._x = this.radiusW;
		}
		if(this._x>=Stage.width - this.radiusW){
			this._x = Stage.width - this.radiusW;
		}
		this._x += this.xVel; 
		this._y += this.yVel; 
		this.yVel *= this.fr;
		this.xVel *= this.fr;
		if(this.hp<=0){
			this.gotoAndStop(3);
			this.rotation = -45;
			this.dead = true;
			exp = this.attachMovie("explosion","expl",this.getNextHighestDepth());
			exp._xscale = 150;

		}
		for(var i=0;i<=asteroids.length;i++){
			if(asteroids[i].hitTest(this._x, this._y, true) && (asteroids[i]._xscale>= 100 && asteroids[i]._xscale<= 160 ) && !asteroids[i].dead){//   
				if(this.able){//    
					if(this.hp>0){//    
						sHit.start();
						asteroids[i].dead = true;
						asteroids[i].gotoAndStop(2);
						this.hp -= int(asteroids[i]._xscale/5);
						exp = asteroids[i].attachMovie("explosion","expl",asteroids[i].getNextHighestDepth());
						exp._xscale = 80;
						rs.displayField.redScreenField.attachMovie("redScreen","redScreen",0);
						rs._x = stageCoords.x;
						rs._y = stageCoords.y;
						this.xVel = this.xVel+(int(Math.random()*35)-19);//          
						this.yVel = this.yVel+(int(Math.random()*-35)+22);   
							if(this.hp<0){
								this.hp = 0;
						}
					}
				}
			}
		}
	}else{//    
		this._y += 5;//  
		this._x -= 5;
		this._xscale = this._yscale += 1;//  
		this.deathC == undefined ? this.deathC = 0 : this.deathC++;
		if(this.deathC>=30){
			this.removeMovieClip();
			gameOver();
			sFall.start();
			sBg2.stop();
		}
	}
}

function shoot(xx:Number,yy:Number){
	bullet_counter++;
	bb = bulletField.attachMovie("bullet","bullet"+bullet_counter,bullet_counter);
	bullets.push(bb);
	bb._x=xx+int((Math.random()*20)+(Math.random()*-25));
	bb._y=yy+int((Math.random()*-25)+(Math.random()*20));
	bb._xscale = bb._yscale = 80;
	bb.onEnterFrame = bulletStuff;
}
function bulletStuff(){
	if(this._xscale = this._yscale>0){
		this._xscale = this._yscale -= bullet_speed;
	}else{
		this.removeMovieClip();
	}
}

function spawnEnemy(){
	enemies_counter++;// 1 
	enemy = enemyField.attachMovie("ufo","ufo"+enemies_counter, ENEMIES_DEPTH);
	enemies.push(enemy);//   
	enemy._x = stageCoords.x + int(Math.random()*250)-135;//   x b y
	enemy._y = stageCoords.y + int(Math.random()*-150)+85;
	enemy._xscale = enemy._yscale=5;//     
	enemy.dead = false;//  
	enemy.coolDown = 50+int(Math.random()*20)-15;//      
	enemy.attackStr = 10;//    
	enemy.canAttack = false;//       
	enemy.hp = 10;//
	enemy.maxScale = 100;//   
	enemy.canShoot = 0;// 
	enemy.able = true;//  
	enemy.recovery = 0;//
	enemy.recoveryTime = 20;
	enemy.lifeTime = 0;//         
	enemy.maxLifeTime = 150;//  
	enemy.gone = false;// 
	enemy.onEnterFrame = controlEnemy;//
	ENEMIES_DEPTH--;//   



}

function controlEnemy(){
	if(!this.dead){// 
		if(!this.able){//  
			this.recovery++;// 
			if(this.recovery>=this.recoveryTime){//  
				this.recovery = 0;// 
				this.able = true;//   
			}
		}
		if(this._xscale<=this.maxScale && !this.canAttack){//       
		this._xscale = this._yscale += 0.5;// 
		if(this.gone){//   
			this._y -=10;// 
			this._xscale = this._yscale +=2;
			if(this._y <= -5){//   
				this.swapDepths(100);//     100    removeMovieclip       
				this.removeMovieClip();
			}
		}

	}else{
		this.canAttack = true;//   
	}
		if(this.canAttack && !this.gone){//      
			this.canShoot++;//  
			this.lifeTime++;//  
			if(this.lifeTime >= this.maxLifeTime){//       
				this.canAttack = false;//  
				this.maxScale = 300;// 
				this.gone = true; //  
				//this.swapDepths(100);
				this.removeMovieClip();

			}
			if(this.canShoot >= this.coolDown && this.able){//         
				this.canShoot = 0; //  
				ship.hp -=  this.attackStr; //   -
				ff = this.ship.attachMovie("shoot_fire", "shoot_fire", 0 );//  
				ff._xscale = ff._yscale = 150;
				ff._y -= 5;

				rs = displayField.redScreenField.attachMovie("redScreen","redScreen",0 );//   
				rs._x = stageCoords.x;
				rs._y = stageCoords.y;
				sLaser2.start();
			}
		}
		for(var i = 0; i < bullets.length; i++){//    
			if(bullets[i].hitTest(this)){//  
				if(this.able && this._xscale >= 50 ){//       
					if(this.hp > 0){//   
						this.hp -= shipAttackStr;// 
						this.gotoAndStop(2);//  
						this.able = false;//   
						sHit.start();

					}else{
						this.dead = true;//   0
						this.gotoAndStop(3);//   

						exp = this.attachMovie("explosion", "expl", this.getNextHighestDepth());//  
						exp._xscale = 150;
						score += 30;

					}

				}
			}

		}

	}else{
		this._y += 7;//    
		this._xscale = this._yscale += 2;// 
		this.deathC == undefined ? this.deathC = 0 : this.deathC++;//       
			if(this.deathC>=30){
				this.swapDepths(100);
				this.removeMovieClip();
			}

		}

	}

	function createDisplay(){
		hp_txt = displayField.hpField.createTextField("hp_txt", 0, 20, 20, 300, 60);//  
		score_txt = displayField.scoreField.createTextField("score_txt", 1, 480, 20, 300, 60);
		hp_txt.selectable = score_txt.selectable = false;//   
		hp_txt.setNewTextFormat(myFormat);
		score_txt.setNewTextFormat(myFormat);
		hp_txt.text = ": "+ship.hp+"%";
		score_txt.text = ": "+score;
		displayField.onEnterFrame = updateDisplay;

	}

	function updateDisplay(){
		hp_txt.text = ": "+ship.hp+"%";
		score_txt.text = ": "+score;
	}


	function spawnBonus(){
		var a = int(Math.random()*15)+1;//    
			if(a%3 == 0){
				bonus_counter++;
				BONUSES_DEPTH++;
				bonus = bonusField.attachMovie("bonus", "bonus"+bonus_counter, BONUSES_DEPTH);
				bonus.TYPE = "MONEY";
				bonus._xscale = bonus._yscale = 10;
				bonus._x = stageCoords.x+int(Math.random()*stageCoords.rand2+Math.random()*-stageCoords.rand2);
				bonus._y = stageCoords.y+int(Math.random()*-stageCoords.rand+Math.random()*stageCoords.rand);
			}
			if(a%7 == 0){//  %    
				bonus_counter++;
				BONUSES_DEPTH++;
				bonus = bonusField.attachMovie("bonus", "bonus"+bonus_counter, BONUSES_DEPTH);
				bonus.TYPE = "BOMB";
				bonus._xscale = bonus._yscale = 10;
				bonus._x = stageCoords.x+int(Math.random()*stageCoords.rand2+Math.random()*-stageCoords.rand2);
				bonus._y = stageCoords.y+int(Math.random()*-stageCoords.rand+Math.random()*stageCoords.rand);
			}
			if(a%8 == 0){//  %    
				bonus_counter++;
				BONUSES_DEPTH++;
				bonus = bonusField.attachMovie("bonus", "bonus"+bonus_counter, BONUSES_DEPTH);
				bonus.TYPE = "HEALTH";
				bonus._xscale = bonus._yscale = 10;
				bonus._x = stageCoords.x+int(Math.random()*stageCoords.rand2+Math.random()*-stageCoords.rand2);
				bonus._y = stageCoords.y+int(Math.random()*-stageCoords.rand+Math.random()*stageCoords.rand);
			}
			if(a%5 == 0){//  %    
				bonus_counter++;
				BONUSES_DEPTH++;
				bonus = bonusField.attachMovie("bonus", "bonus"+bonus_counter, BONUSES_DEPTH);
				bonus.TYPE = "TELEK";
				bonus._xscale = bonus._yscale = 10;
				bonus._x = stageCoords.x+int(Math.random()*stageCoords.rand2+Math.random()*-stageCoords.rand2);
				bonus._y = stageCoords.y+int(Math.random()*-stageCoords.rand+Math.random()*stageCoords.rand);
			}
				switch(bonus.TYPE){
				case "MONEY":
				bonus.gotoAndStop(1);
				break;
				case "BOMB":
				bonus.gotoAndStop(2);
				break;
				case "HEALTH":
				bonus.gotoAndStop(3);
				break;
				case "TELEK":
				bonus.gotoAndStop(4);
				break;
			}
			if(bonus.yy <= stageCoords.y){
				bonus.yMov = "u";
			}else{
				bonus.yMov = "d";
			}
			if(bonus.xx <= stageCoords.x){
				bonus.xMov = "l";
			}else{
				bonus.xMov = "r";
			}
			bonus.onEnterFrame = bonusStuff;

	}

	function bonusStuff(){
		this._xscale = this._yscale += bonus_speed;
			switch(this.yMov){
				case "u":
				this._y -= bonus_speed;
				break;
				case "d":
				this._y += bonus_speed;
				break;
			}switch(this.xMov){
				case "l":
				this._x -= bonus_speed;
				break;
				case "r":
				this._x += bonus_speed;
				break;
			}
			if(this._xscale>= 70){
				this.removeMovieClip();
			}

		if(this.hitTest(ship._x, ship._y, true ) && (this._xscale>=35 && this._yscale<=65)){//     

				switch(this.TYPE){
					case "MONEY":
					score += 100;
					ys = displayField.greenScreenField.attachMovie("yellowScreen", "yellowScreen", 0);
					ys._x = stageCoords.x;
					ys._y = stageCoords.y;
					sPower.start();
					this.removeMovieClip();
					break;
						case"BOMB":
							for(var i=0;i<enemies.length;i++){
								if(enemies[i]._xscale>= 70){
									ws = displayField.whiteScreenField.attachMovie("whiteScreen", "whiteScreen", 0);
										ws._x = stageCoords.x;
											ws._y = stageCoords.y;
											if(!enemies[i].dead){
												enemies[i].swapDepths(100);
												enemies[i].dead = true;
												enemies[i].gotoAndStop(3);
												exp = enemies[i].attachMovie("explosion", "expl",enemies[i].getNextHighestDepth());
												exp._xscale = 150;
												score += 25;
											}
									}
							}
							for(var i=0;i<asteroids.length;i++){
								if(asteroids[i]._xscale>= 60){
									ws = displayField.whiteScreenField.attachMovie("whiteScreen", "whiteScreen", 0);
										ws._x = stageCoords.x;
											ws._y = stageCoords.y;
											if(!asteroids[i].dead){
												asteroids[i].dead = true;
												asteroids[i].gotoAndStop(2);
												exp = asteroids[i].attachMovie("explosion", "expl",asteroids[i].getNextHighestDepth());
												exp._xscale = 150;
												score += 10;
											}
									}
							}
							sPower.start();
								this.removeMovieClip();
												break;


					case"HEALTH":
						bs = displayField.blueScreenField.attachMovie("blueScreen", "blueScreen", 0);
						bs._x = stageCoords.x;
						bs._y = stageCoords.y;
						ship.hp += 20;
							if(ship.hp>100){
								ship.hp = 100;
							}
							sPower.start();
							this.removeMovieClip();
							break;

							case"TELEK":
							for(var i=0;i<enemies.length;i++){
								if(enemies[i]._xscale>= 80){
									ws = displayField.whiteScreenField.attachMovie("bonPika", "bonPika", 0);
										ws._x = stageCoords.x;
											ws._y = stageCoords.y;
											if(!enemies[i].dead){
												enemies[i].swapDepths(100);
												enemies[i].dead = true;
												enemies[i].gotoAndStop(3);
												exp = enemies[i].attachMovie("explosion", "expl",enemies[i].getNextHighestDepth());
												exp._xscale = 150;
												score += 25;

											}
									}
							}
							for(var i=0;i<asteroids.length;i++){
								if(asteroids[i]._xscale>= 60){
									ws = displayField.whiteScreenField.attachMovie("bonPika", "bonPika", 0);
										ws._x = stageCoords.x;
											ws._y = stageCoords.y;
											if(!asteroids[i].dead){
												asteroids[i].dead = true;
												asteroids[i].gotoAndStop(2);
												exp = asteroids[i].attachMovie("explosion", "expl",asteroids[i].getNextHighestDepth());
												exp._xscale = 150;
												score += 10;
											}
									}
							}
							sPower.start();
								this.removeMovieClip();
												break;

					}	
				}	   
			}

	function spawnAsteroid(){
		asteroid_counter++;
		ast = asteroidField.attachMovie("aster1", "aster1"+asteroid_counter, ASTEROIDS_DEPTH);
		asteroids.push(ast);
		ast._xscale = ast._yscale = 10+int(Math.random()*8);
		ast._x = int(Math.random()*Stage.width)+5;
		ast._y = int(Math.random()*Stage.height)-8;
		ast.asts.gotoAndStop(int(Math.random()*ast.asts._totalframes));
		ast.rot = int(Math.random()*10)-8;
		ast.maxScale = 180;
		ast.dead = false;
		ASTEROIDS_DEPTH++;
		ast.onEnterFrame = asteroidStuff;


	}
	function asteroidStuff(){
		if(!this.dead){
			this._xscale = this._yscale += asteroid_speed;
			this._rotation += this.rot;
				if(this._xscale >= this.maxScale){
					this.removeMovieClip();
				}
					for(var i=0; i <= bullets.length;i++){//      
						if(bullets[i].hitTest(this) && (this._xscale >=100 && this._xscale  <=160)){//        100-160  
							this.dead = true;
							this._rotation = 0;
							this.gotoAndStop(2);
							exp = this.attachMovie("explosion", "expl", this.getNextHighestDepth());
							exp._xscale = 80;
							score += 10;
						}
					}

		}else{ //  
			this._y += 7;//  
			this.deathC == undefined ? this.deathC = 0 : this.deathC++;
				if(this.deathC>=20){
					this.removeMovieClip();
			}
		}
	}

	function gameOver(){
		starField.removeMovieClip();
		bgField.swapDepths(100);//        -2
		bgField.removeMovieClip();
		shipField.removeMovieClip();
		bulletField.removeMovieClip();
		enemyField.removeMovieClip();
		bonusField.removeMovieClip();
		displayField.removeMovieClip();
		asteroidField.removeMovieClip();
		clearInterval(spawnInterval); // 
		clearInterval(bonusInterval);
		clearInterval(asteroidInterval);
		gotoAndStop("lose");//  3 
	}

createBG(175,stageCoords.x,stageCoords.y);
createShip(stageCoords.x,stageCoords.y+100);
